import React,{Component} from "react";
import {Table} from "react-bootstrap";

export class Churn extends Component{

    constructor(props){
        super(props);
        this.state={deps:[]}
    }

    refreshList(){
        fetch(process.env.REACT_APP_API+'churn')
        .then(response=>response.json())
        .then(data=>{
            this.setState({deps:data});
        })
    }

    componentDidMount(){
        this.refreshList();
    }

    componentDidUpdate(){
        this.refreshList();
    }
    
    render(){
        const {deps}=this.state; 
        return(
            <div>
                <Table className="mt-4" striped bordered hover size="sm">
                    <thead>
                        <th>Serial Number</th>
                        <th>ModelPred</th>
                        <th>Review</th>
                        <th>Final</th>
                    </thead>
                    <tbody>
                        {deps.map(dep=>
                        <tr key={dep.id}>
                            <td>{dep.ModelPRed}</td>
                            <td>{dep.Review}</td>
                            <td>{dep.Final}</td>
                        </tr>
                        )}
                    </tbody>
                </Table>
            </div>
        )
    }
}
